using UnityEngine; using UnityStandardAssets.Characters.FirstPerson;  public class NetworkPlayerManager : Photon.MonoBehaviour {     PhotonController photonController;     public int life = 100;      void Start()     {         if (photonView.isMine)         {             GetComponent<Rigidbody>().useGravity = true;             GetComponent<FirstPersonController>().enabled = true;             GetComponentInChildren<ScoreManager>().enabled = true;             GetComponentInChildren<FiringController>().enabled = true;             GetComponentInChildren<Camera>().enabled = true;             GetComponentInChildren<ParamScript>().enabled = true;             GetComponentInChildren<Canvas>().enabled = true;             photonController = (PhotonController)FindObjectOfType(typeof(PhotonController));          }     }      void OnPhotonSerializeView(PhotonStream stream, PhotonMessageInfo info)     {         if (stream.isWriting)         {             stream.SendNext(life);         }         else         {             life = (int)stream.ReceiveNext();         }     }      [PunRPC]     public void GetShot()     {         life -= 25;          if (life == 0 && photonView.isMine)         {             photonController.StartSpawnProcess();             PhotonNetwork.Destroy(gameObject);         }     } }  