using System.Collections; using UnityEngine;  public class PhotonController : Photon.MonoBehaviour {     [SerializeField] GameObject player;     [SerializeField] Camera sceneCamara;     [SerializeField] GameObject stage;     float stageX;     float stageZ;      void Start()     {         PhotonNetwork.ConnectUsingSettings("0.1");         stageX = stage.GetComponent<Renderer>().bounds.size.x;         stageZ = stage.GetComponent<Renderer>().bounds.size.z;     }      void OnJoinedLobby()     {         PhotonNetwork.JoinRandomRoom();     }      void OnPhotonRandomJoinFailed()     {         PhotonNetwork.CreateRoom(null);     }      void OnJoinedRoom()     {         StartSpawnProcess();     }      public void StartSpawnProcess()     {         sceneCamara.enabled = true;         StartCoroutine(SpawnPlayer());     }      public IEnumerator SpawnPlayer()     {         yield return new WaitForSeconds(3.0f);         Vector3 instantiatePosition=stage.transform.position + new Vector3(Random.Range(-stageX / 2, stageX / 2), 1, Random.Range(-stageZ / 2, stageZ / 2));         PhotonNetwork.Instantiate(player.name,instantiatePosition,Quaternion.identity,0);         sceneCamara.enabled = false;     }  } 